import React, { useState, useEffect, useRef } from 'react';
import axios from 'axios';
import ReactPlayer from 'react-player';

const VideoDetail = ({ videoId }) => {
  const [video, setVideo] = useState(null);
  const [error, setError] = useState(null);
  const [playbackRate, setPlaybackRate] = useState(1.0); // The playback rate of the video.
  const [volume, setVolume] = useState(1.0); // The volume of the video.
  const videoRef = useRef(null);

  useEffect(() => {
    const fetchVideo = async () => {
      try {
        const response = await axios.get('https://take-home-assessment-423502.uc.r.appspot.com/api/videos/single', { params: { video_id: videoId } });
        console.log('API Response:', response.data); // Debug log

        // Assuming the response structure includes a video object
        const videoData = response.data.video;

        if (videoData) {
          console.log('Setting video:', videoData);
          setVideo(videoData);
        } else {
          setError('Video data not found');
        }
      } catch (error) {
        console.error('Error fetching video:', error);
        setError(`Failed to fetch video: ${error.message}`);
      }
    };
    fetchVideo();
  }, [videoId]);

  // const handleFullScreen = () => {
  //   if (videoRef.current) {
  //     if (videoRef.current.requestFullscreen) {
  //       videoRef.current.requestFullscreen();
  //     } else if (videoRef.current.mozRequestFullScreen) { /* Firefox */
  //       videoRef.current.mozRequestFullScreen();
  //     } else if (videoRef.current.webkitRequestFullscreen) { /* Chrome, Safari and Opera */
  //       videoRef.current.webkitRequestFullscreen();
  //     } else if (videoRef.current.msRequestFullscreen) { /* IE/Edge */
  //       videoRef.current.msRequestFullscreen();
  //     }
  //   }
  // };

  const handleFullScreen = () => {
    if (videoRef.current) {
      videoRef.current.requestFullscreen();
    }
  };

  const handlePlaybackSpeed = (e) => {
    if (videoRef.current) {
      videoRef.current.playbackRate = e.target.value;
    }
  };

  const handleVolumeChange = (e) => {
    if (videoRef.current) {
      videoRef.current.volume = e.target.value;
    }
  };

  if (error) {
    return <div>{error}</div>;
  }

  if (!video) {
    return <div>Loading...</div>;
  }

  return (
    <div className="video-player">
      
      <ReactPlayer 
        className='video-list__card-video'
        ref={videoRef}
        url={video.video_url} 
        controls 
        width="auto" 
        height="auto"/>
      {/* <video id = {video.video_id} controls>
        <source src={video.video_url} type="video/webm"/>
        Your browser does not support the video tag.
      </video> */}
      <h2>{video.title}</h2>
      <button onClick={handleFullScreen}>Full Screen</button>
      <label>
        Playback Speed:
        <select onChange={handlePlaybackSpeed} defaultValue="1">
          <option value="0.5">0.5x</option>
          <option value="1">1x</option>
          <option value="1.5">1.5x</option>
          <option value="2">2x</option>
        </select>
      </label>
      <label>
        Volume:
        <input type="range" min="0" max="1" step="0.1" onChange={handleVolumeChange} defaultValue="1" />
      </label>
      <p>{video.created_at}</p>
      <p>{video.user_id}</p>
      <p>{video.description}</p>
      <p>{video.num_comments}</p>
      
    </div>
  );
};

export default VideoDetail;
