import React, { useState } from 'react';
import axios from 'axios';
import "./CommentInput.scss";
import Mohan from "../../../src/assets/images/Mohan-muruge.jpg";

function CommentInput({ videoId, onCommentAdded }) {
  const [comment, setComment] = useState('');

  const handleCommentChange = (e) => setComment(e.target.value);

  const handleCommentSubmit = (e) => {
    e.preventDefault();
    axios.post('https://take-home-assessment-423502.uc.r.appspot.com/api/videos/comments', { video_id: videoId, content: comment, user_id: "john_smith" })
      .then(response => {
        setComment('');
        onCommentAdded(response.data);
      })
      .catch(error => console.error('Error adding comment:', error));
  };

  return (
    <section className="commentinput">
      <h4 className="commentinput__label">JOIN THE CONVERSATION</h4>
      <div className="commentinput__tab">
        <img className="commentinput__image" src={Mohan} alt="Avatar" />
        <form className="commentinput__tab-container" onSubmit={handleCommentSubmit}>
          <input 
            className="commentinput__tab-container-input" 
            type="text" 
            placeholder="Add a new comment" 
            value={comment}
            onChange={handleCommentChange}
          />
          <button className="commentinput__tab-container-button">COMMENT</button>
        </form>
      </div>
    </section>
  );
}

export default CommentInput;
