import React, { useState } from 'react';
import axios from 'axios';

const CreateComment = ({ videoId }) => {
  const [content, setContent] = useState('');
  const [error, setError] = useState(null);
  const [success, setSuccess] = useState(null);

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const response = await axios.post('https://take-home-assessment-423502.uc.r.appspot.com/api/videos/comments', { video_id: videoId, user_id: '123', content });
      console.log('Comment created:', response.data);
    } catch (error) {
      console.error('Error creating comment:', error);
    }
  };

  return (
    <form onSubmit={handleSubmit} className="create-comment">
      <div>
        <label>Comment</label>
        <input type="text" value={content} onChange={(e) => setContent(e.target.value)} placeholder='Add a comment'/>
      </div>
      <button type="submit" className="button-gold">Add Comment</button>
      {error && <div className="error">{error}</div>}
      {success && <div className="success">{success}</div>}
    </form>
  );
};

export default CreateComment;
