import React from 'react';
import "./CommentDisplay.scss";
import placeholder from "../../../src/assets/images/placeholder-image-icon-12.jpg";

function CommentDisplay({ comments }) {
  return (
    <section className="commentdisplay">
      {comments.map((comment) => (
        <div className="commentdisplay__comment" key={comment.id}> 
          <img className="commentdisplay__comment-image" src={placeholder} alt="Avatar" />
          <div className="commentdisplay__comment-column">
            <div className="commentdisplay__comment-column-content">
              <h4 className="commentdisplay__comment-column-content-name">{comment.name}</h4>
              <p className="commentdisplay__comment-column-content-timestamp">{new Date(comment.timestamp).toLocaleDateString()}</p>
            </div>
            <p className="commentdisplay__comment-column-text">{comment.comment}</p>
          </div>
        </div>
      ))}
    </section>
  );
}

export default CommentDisplay;
