import React, { useState } from 'react';
import { useHistory } from 'react-router-dom';
import axios from 'axios';
import "./UploadPage.scss";
import thumbnail from '../../../src/assets/images/Upload-video-preview.jpg';

function UploadPage() {
    const [title, setTitle] = useState('');
    const [description, setDescription] = useState('');
    const history = useHistory();

    const handleTitleChange = (e) => setTitle(e.target.value);
    const handleDescriptionChange = (e) => setDescription(e.target.value);

    const handleFormSubmit = (e) => {
        e.preventDefault();
        axios.post('/api/videos', {
            user_id: 'user123',
            title,
            description,
            video_url: 'http://example.com/video.mp4'
        })
            .then((response) => {
                alert('Your video has been uploaded!');
                history.push('/');
            })
            .catch((error) => console.error('Error uploading video:', error));
    };

    return (
        <div className="upload">
            <h1 className="upload__page-title">Upload Video</h1>
            <h4 className="upload__image-label">VIDEO THUMBNAIL</h4>
            <img className="upload__page-image" src={thumbnail} alt="" />

            <form className="upload__form" onSubmit={handleFormSubmit}>
                <h4 className="upload__form-label">TITLE YOUR VIDEO</h4>
                <input 
                    className="upload__form-input" 
                    type="text" 
                    placeholder="Add a title to your video" 
                    value={title}
                    onChange={handleTitleChange}
                />
                <h4 className="upload__form-label">ADD A VIDEO DESCRIPTION</h4>
                <input 
                    className="upload__form-description"
                    type="text"
                    placeholder="Add a description to your video" 
                    value={description}
                    onChange={handleDescriptionChange}
                />
                <button className="upload__form-publish-button" type="submit">PUBLISH</button>
                <button className="upload__form-cancel-button" type="button" onClick={() => history.push('/')}>CANCEL</button>
            </form>
        </div>
    );
}

export default UploadPage;
