import React, { useState } from 'react';
import axios from 'axios';
import './CreateComment.scss';

const CreateComment = ({ videoId }) => {
  const [content, setContent] = useState('');
 

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const response = await axios.post('https://take-home-assessment-423502.uc.r.appspot.com/api/videos/comments', { video_id: videoId, user_id: '123', content });
      console.log('Comment created:', response.data);
    } catch (error) {
      console.error('Error creating comment:', error);
    }
  };

  return (

    <section className="commentinput">


      <form onSubmit={handleSubmit} className="commentinput__tab-container">

        <h5 className="commentinput__tab-label">JOIN THE CONVERSATION</h5>

        <input className="commentinput__tab-container-input" type="text" value={content} onChange={(e) => setContent(e.target.value)} placeholder='Add a comment'/>

        <button type="submit" className="commentinput__tab-container-button">Add Comment</button>

      </form>
    </section>

    
  );
};

export default CreateComment;
