import React, { useState } from 'react';
import axios from 'axios';

const CreateVideo = () => {
    const [userId, setUserId] = useState('');
    const [title, setTitle] = useState('');
    const [description, setDescription] = useState('');
    const [videoUrl, setVideoUrl] = useState('');
    const [error, setError] = useState(null);
    const [success, setSuccess] = useState(null);

    const handleSubmit = async (e) => {
        e.preventDefault();
        try {
            const response = await axios.post('https://take-home-assessment-423502.uc.r.appspot.com/api/videos', { user_id: userId, title, description, video_url: videoUrl });
            console.log('Video created:', response.data);
            setSuccess('Video created successfully');
            setUserId('');
            setTitle('');
            setDescription('');
            setVideoUrl('');
        } catch (error) {
            console.error('Error creating video:', error);
            setError(`Failed to create video: ${error.message}`);
        }
    };

    return (
        <form onSubmit={handleSubmit} className="create-video">
            <div className="input-group">
                <label>User</label>
                <input  type="text" value={userId} onChange={(e) => setUserId(e.target.value)} />
            </div>
            <div className="input-group">
                <label>Title</label>
                <input  type="text" value={title} onChange={(e) => setTitle(e.target.value)} />
            </div>
            <div className="input-group">
                <label>Description</label>
                <input  type="text" value={description} onChange={(e) => setDescription(e.target.value)} />
            </div>
            <div className="input-group">
                <label>Video URL</label>
                <input  type="text" value={videoUrl} onChange={(e) => setVideoUrl(e.target.value)} />
            </div>
            <button  className="button-gold" type="submit">Create Video</button>
            <button >Cancel</button> 
            {error && <div className="error">{error}</div>}
            {success && <div>{success}</div>}
        </form>
    );
};

export default CreateVideo;
