import React, { useState, useEffect, useRef } from 'react';
import axios from 'axios';
import ReactPlayer from 'react-player';
import screenfull from 'screenfull';
import CreateComment from './CreateComment';

const VideoDetail = ({ videoId }) => {
  const [video, setVideo] = useState(null);
  const [error, setError] = useState(null);
  const [playbackRate, setPlaybackRate] = useState(1.0); // The playback rate of the video.
  const [volume, setVolume] = useState(1.0); // The volume of the video.
  const videoRef = useRef(null);

  useEffect(() => {
    const fetchVideo = async () => {
      try {
        const response = await axios.get('https://take-home-assessment-423502.uc.r.appspot.com/api/videos/single', { params: { video_id: videoId } });
        console.log('API Response:', response.data); // Debug log

        // Assuming the response structure includes a video object
        const videoData = response.data.video;

        if (videoData) {
          console.log('Setting video:', videoData);
          setVideo(videoData);
        } else {
          setError('Video data not found');
        }
      } catch (error) {
        console.error('Error fetching video:', error);
        setError(`Failed to fetch video: ${error.message}`);
      }
    };
    fetchVideo();
  }, [videoId]);

  const handleFullScreen = () => {
    if (screenfull.isEnabled) {
      screenfull.request(videoRef.current.wrapper);
    }
  };

  const handlePlaybackSpeed = (e) => {
    setPlaybackRate(parseFloat(e.target.value));
  };

  const handleVolumeChange = (e) => {
    setVolume(parseFloat(e.target.value));
  };

  const formatDate = (timestamp) => {
    const date = new Date(timestamp);
    const options = { 
      year: '2-digit', 
      month: '2-digit', 
      day: '2-digit', 
      hour: '2-digit', 
      minute: '2-digit', 
      hour12: true 
    };
    return date.toLocaleString('en-US', options);
  };

  if (error) {
    return <div>{error}</div>;
  }

  if (!video) {
    return <div>Loading...</div>;
  }

  return (

    
    <div className="videodescription">
      <ReactPlayer 
        ref={videoRef}
        url={video.video_url} 
        controls 
        width="auto"
        playbackRate={playbackRate}
        volume={volume}/>
      

      <h2 className="videodescription__title">
        {video.title}
      </h2>

      <button onClick={handleFullScreen}>Full Screen</button>

      <label>
        Playback Speed:
        <select onChange={handlePlaybackSpeed} defaultValue="1">
          <option value="0.5">0.5x</option>
          <option value="1">1x</option>
          <option value="1.5">1.5x</option>
          <option value="2">2x</option>
        </select>
      </label>

      <label>
        Volume:
        <input type="range" min="0" max="1" step="0.1" onChange={handleVolumeChange} defaultValue="1" />
      </label>

      <p className="videodescription__tab-container-timestamp">{formatDate(video.created_at)}</p>

      <p className="videodescription__tab-container-channel">By {video.user_id}</p>

      <p className="videodescription__description">{video.description}</p>
      {/* <CreateComment/> */}

      <h3>{video.num_comments}</h3>
      
    </div>
  );
};

export default VideoDetail;
