import React from 'react';
import "./VideoDescription.scss";
import viewsicon from "../../../src/assets/icons/views.svg";
import likesicon from "../../../src/assets/icons/likes.svg";

function VideoDescription({ selectedVideo }) {
  return (
    <section className="videodescription">
      <h1 className="videodescription__title">{selectedVideo.title}</h1>
      <div className="videodescription__tab">
        <div className="videodescription__tab-container">
          <p className="videodescription__tab-container-channel">By {selectedVideo.channel}</p>
          <p className="videodescription__tab-container-timestamp">{new Date(selectedVideo.timestamp).toLocaleDateString()}</p>
        </div>
        <div className="videodescription__tab-container">
          <p className="videodescription__tab-container-views">
            <img className="videodescription__tab-container-views-icon" src={viewsicon} alt="views icon"/>
            {selectedVideo.views}
          </p>
          <p className="videodescription__tab-container-likes">
            <img className="videodescription__tab-container-likes-icon" src={likesicon} alt="likes icon"/>
            {selectedVideo.likes}
          </p>
        </div>
      </div>
      <p className="videodescription__description">{selectedVideo.description}</p>
    </section>
  );
}

export default VideoDescription;
