import React, { useContext, useEffect, useState } from 'react';
import axios from 'axios';
import { VideoContext } from '../contexts/VideoContext';

const VideoList = () => {
  const { videos, setVideos } = useContext(VideoContext);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchVideos = async () => {
      try {
        const response = await axios.get('https://take-home-assessment-423502.uc.r.appspot.com/api/videos', { params: { user_id: '123' } });
        console.log('API Response:', response.data); // Debug log

        // Adjust this based on the actual structure of the response
        const videoArray = response.data.data || response.data;
        
        if (Array.isArray(videoArray)) {
          console.log('Setting videos:', videoArray);
          setVideos(videoArray);
        } else {
          setError('Videos data is not an array');
        }
      } catch (error) {
        console.error('Error fetching videos:', error);
        setError(`Failed to fetch videos: ${error.message}`);
      }
    };
    fetchVideos();
  }, [setVideos]);

  if (error) {
    return <div>{error}</div>;
  }

  return (
    <div>
      <h1>Video Listings</h1>
      {videos.map(video => (
        <div key={video.id}>
          <h3>{video.title}</h3>
          <p>{video.description}</p>
        </div>
      ))}
    </div>
  );
};

export default VideoList;
